import React from 'react'

export default class Select extends React.Component {
    render(){
        const div = document.querySelectorAll('div');
        document.querySelector('select').onchange=function(){
            let n = this.selectedIndex;
            [].forEach.call(div, function(el, i){
                el.style.display = n && i !== n-1 ?  'none':'block';
            });
        };
        return(
            <select id="App-content-select">
                <option value="22">Age user 22</option>
                <option value="33">Age user 33</option>
                <option value="44">Age user 44</option>
            </select>
        )
    }
}
